import React from 'react';
import Proptypes from 'prop-types';

class Tag extends React.Component {

    getStyle = (filtered) => {
        return {
            background: filtered ? 'red' : 'black'
        }
    }

    render() {
        const { id, name, filtered } = this.props.tag;

        return(
            <div class="tag" style={this.getStyle(filtered)} onClick={this.props.clickTag.bind(this, id)}>{name}
                <button class="delete is-small" onClick={this.props.deleteTag.bind(this, id)}></button>
            </div>
        )
    }
}

Tag.prototypes = {
    tag: Proptypes.object.isRequired
}

export default Tag;
