import React from 'react';


class SearchBar extends React.Component {
    state = {
        stock: ''
    }


    addTag() {
        // this is where add stock to taglist.
    }

    onChange = (e) => this.setState({

    })

    onSubmit = (e) => {
        // stop submitting to default file
        e.preventDefault();

        // when hit search, add stock to stock taglist 
        // this.props.addTag();
        alert("I added" + this.state.stock + "to list!");

        // clear input
        this.setState({ stock: '' });
    }

    render(){
        return(
                <form onSubmit={this.onSubmit}>
                    <div class="field is-grouped">
                        <p class="control is-expanded">
                            <input 
                            class="input" 
                            type="text" 
                            name="stock"
                            value={this.state.stock}
                            onChange={this.onChange}
                            placeholder="$StockTeets"
                            />
                        </p>
                        <p class="control">
                            <a 
                            class="button is-light"
                            type="submit"
                            value="submit"
                            >Search</a>
                        </p>
                    </div>
                </form>

        )
    }
}

export default SearchBar;
