import React from 'react';
import SearchBar from '../components/SearchBar'
import TweetBoard from '../components/TweetBoard';
import TagList from '../components/TagList';
import axios from 'axios';


class Home extends React.Component {
    state = {
        stock: 'AMD',
        tags: [],
        tweets: []
    }

    render() {   
        return(
            <div>
            <section class="hero is-info">
            <div class="hero-body">
                <div class="container">
                    {/* header */}
                    <h1 class="title">Stock Tweets</h1>
                    <h2 class="subtitle">See the latest tweets on your favorite stocks!</h2>
                    <nav class="level">

                        {/* search field */}
                        <div class="level-left">
                            <div class="level-item">
                                <SearchBar onSubmit={this.onSubmit} onChange={this.onChange} />
                            </div>
                        </div>

                        {/* taglist */}
                        <div class="level-right">
                            <div class="level-item">
                                <div class="tags">
                                <TagList tags={this.state.tags} deleteTag={this.deleteTag} filterTag={this.filterTag} />
                                </div>
                            </div>
                        </div>

                    </nav>
                </div>
            </div>
            </section>

            {/* tweets section */}
            <div class="section">
                <div class="container">
                <div class="tile is-ancestor">
                    <div class="tile is-vertical">
                        <div class="tile">
                            <TweetBoard tweets={this.state.tweets} />
                        </div>
                    </div>
                </div>                
                </div>
            </div>
            </div>
)}

    addTag(stock) {
        const newTag = {
            id: Math.random,
            name: stock
        }
        this.setState({ tags: [...this.state.tags.concat(newTag)]});
    }

    onSubmit = (e) => {
        e.preventDefault();
        this.addTag(this.state.stock);
        this.setState({ stock: '' });
    }

    onChange = (e) => this.setState({
        stock: e.target.value
    })

    // show all the tags that don't equal the one you deleted
    deleteTag = (id) => {
        this.setState({ tags: [...this.state.tags.filter(tag => tag.name !== stock)] });
        alert('i removed ' +  + 'from board');
    }

    filterTag = (id) => {
        alert('i clicked it')
    }

    fetchStockTweets(url) {
        const Httpreq = new XMLHttpRequest()
        Httpreq.open('GET', url, false);
        Httpreq.send(null);
        var data = JSON.parse(Httpreq.responseText);
        var messages = data.messages;
        console.log(data)

        this.setState({ tweets: messages })
    }

    componentDidMount() {
        let url = "https://api.stocktwits.com/api/2/streams/symbol/" + this.state.stock + ".json?limit=4";
        this.fetchStockTweets(url);
    }

}
export default Home;
