import React from 'react';


class SearchBar extends React.Component {

    addTag() {
        // this is where add stock to taglist.
    }

    onSubmit = (e) => {
        // stop submitting to default file
        e.preventDefault();

        // when hit search, add stock to stock taglist 
        this.props.addTag();

        // clear input
        this.setState({ stock: '' });
    }

    render(){
        return(
                <form onSubmit={this.onSubmit}>
                <div class="field is-grouped">
                    <p class="control is-expanded">
                        <input 
                        class="input" 
                        type="text" 
                        placeholder="$StockTeets"
                        />
                    </p>
                    <p class="control">
                        <a class="button is-light">Search</a>
                    </p>
                </div>
                </form>

        )
    }
}

export default SearchBar;
