import React from 'react';
import SearchBar from '../components/SearchBar'
import TweetBoard from '../components/TweetBoard';
import TagList from '../components/TagList';
import axios from 'axios';


class Home extends React.Component {
    state = {
        stock: '',
        tags: [
            {id: 1, name: "APPL"},
            {id: 2, name: "MSFT"},
            {id: 3, name: "BABA"}
        ],
        tweets: [
            {
                id: 18037207846,
                body: "$AAPL - Upcoming quarterly earning report is on October 30, 2019. Apple is projected to stay under $241 in November http://dlvr.it/RGCYMD",
                created_at: "2019-10-15T06:37:05Z"
            },
            {
                id: 1803720789,
                body: "$AAPL - Upcoming quarterly earning report is on October 30, 2019. Apple is projected to stay under $241 in November http://dlvr.it/RGCYMD",
                created_at: "2019-10-15T06:37:05Z"
            },
            {
                id: 1803723453,
                body: "$AAPL - Upcoming quarterly earning report is on October 30, 2019. Apple is projected to stay under $241 in November http://dlvr.it/RGCYMD",
                created_at: "2019-10-15T06:37:05Z"
            },

        ],
        data: []
    }

    // make call to backend - retrieve tweets api
    componentDidMount() {
        // axios.get('https://api.stocktwits.com/api/2/streams/symbol/AAPL.json?limit=3')
        axios.get('/hey')
        .then(response => this.setState({ data: response.data }))
        // fetch('/hey');
    }

    onSearch = (e) => {
        // stop submitting to default file
        e.preventDefault();

        // when hit search, add stock to stock taglist 
        const newTag = {
            id: Math.random,
            name: this.state.stock
        }
        this.setState({ tags: [...this.state.tags.concat(newTag)]});

        // clear input
        this.setState({ stock: '' });
    }

    onChange = (e) => this.setState({
        stock: e.target.value
    })

    // show all the tags that don't equal the one you deleted
    deleteTag = (id) => {
        this.setState({ tags: [...this.state.tags.filter(tag => tag.id !== id)] });
        alert('i removed ' + this.state.tags.name + 'from board');
    }

    render() {   
        return(
            <div>
            <section class="hero is-info">
            <div class="hero-body">
                <div class="container">
                    {/* header */}
                    <h1 class="title">Stock Tweets</h1>
                    <h2 class="subtitle">See the latest tweets on your favorite stocks!</h2>
                    <nav class="level">

                        {/* search field */}
                        <div class="level-left">
                            <div class="level-item">
                                <SearchBar onSearch={this.onSearch} onChange={this.onChange} />
                            </div>
                        </div>

                        {/* taglist */}
                        <div class="level-right">
                            <div class="level-item">
                                <div class="tags">
                                <TagList tags={this.state.tags} deleteTag={this.deleteTag} />
                                </div>
                            </div>
                        </div>

                    </nav>
                </div>
            </div>
            </section>

            {/* tweets section */}
            <div class="section">
                <div class="container">
                <div class="tile is-ancestor">
                    <div class="tile is-vertical">
                        <div class="tile">
                            <TweetBoard tweets={this.state.tweets} />
                        </div>
                    </div>
                </div>                
                </div>
            </div>
            </div>
        )}
}

export default Home;
