import React from 'react';
import Proptypes from 'prop-types';

class Tag extends React.Component {
    render() {
        const { id, name } = this.props.tag;
        return(
            <span class="tag">{name}<button class="delete is-small" onClick={this.props.deleteTag.bind(this, id)}></button></span>
        )
    }
}

// PropTypes
Tag.prototype = {
    tag: Proptypes.object.isRequired
}

export default Tag;
