import React from 'react';
import SearchBar from '../components/SearchBar'
import TweetBoard from '../components/TweetBoard';
import TagList from '../components/TagList';
import axios from 'axios';


class Home extends React.Component {
    state = {
        stock: '',
        tags: [
            {id: 1, name: "APPL"},
            {id: 2, name: "MSFT"}
        ],
        tweets: []
    }

    componentDidMount() {
        axios.get('https://api.stocktwits.com/api/2/streams/symbol/AAPL.json')
        .then(response => this.setState({ tweets: response.data }))
    }

    // show all the tags that don't equal the one you deleted
    deleteTag = (id) => {
        alert("i did it");
        // this.setState({ tags: [...this.state.tags.filter(tag => tag.id !== id)] });
    }

    render() {   
        return(
            <div>
            <section class="hero is-info">
            <div class="hero-body">
                <div class="container">
                    {/* header */}
                    <h1 class="title">Stock Tweets</h1>
                    <h2 class="subtitle">See the latest tweets on your favorite stocks!</h2>
                    <nav class="level">

                        {/* search field */}
                        <div class="level-left">
                            <div class="level-item">
                                <SearchBar />
                            </div>
                        </div>

                        {/* taglist */}
                        <div class="level-right">
                            <div class="level-item">
                                <div class="tags">
                                <TagList tags={this.state.tags} deleteTag={this.deleteTag} />
                                </div>
                            </div>
                        </div>

                    </nav>
                </div>
            </div>
            </section>

            {/* tweets section */}
            <TweetBoard tweets={this.state.tweets}/>
            </div>
        )}
}

export default Home;
